script:
frappe.ui.form.on('Item', {
    validate: function(frm) {
        ensure_unique_selection(frm, 'uoms', 'unit_odd');
        ensure_unique_selection(frm, 'uoms', 'unit_even');
    }
});

function ensure_unique_selection(frm, child_table, fieldname) {
    let selected_rows = frm.doc[child_table].filter(row => row[fieldname]);

    if (selected_rows.length > 1) {
        selected_rows.forEach((row, index) => {
            if (index > 0) {
                row[fieldname] = 0;
            }
        });
        frappe.msgprint(__('Chỉ được chọn 1 sản phẩm là đơn vị chẵn và 1 sản phẩm là đơn vị lẻ '));
        frm.refresh_field(child_table);
    }
}

frappe.ui.form.on('UOM Conversion Detail', {
    unit_odd: function(frm, cdt, cdn) {
        toggle_other_rows(frm, cdt, cdn, 'unit_odd', 'unit_even');
    },
    unit_even: function(frm, cdt, cdn) {
        toggle_other_rows(frm, cdt, cdn, 'unit_even', 'unit_odd');
    }
});

function toggle_other_rows(frm, cdt, cdn, selected_field, other_field) {
    let child = locals[cdt][cdn];

    if (child[selected_field]) {
        frm.doc.uoms.forEach(row => {
            if (row.name !== child.name) {
                row[selected_field] = 0;
            }
            // Khi chọn một dòng là chẵn/lẻ, tự động bỏ tích dòng đó ở trường còn lại
            row[other_field] = (row.name === child.name) ? 0 : row[other_field];
        });
        frm.refresh_field('uoms');
    }
}

frappe.ui.form.on('Sales Order', {
    refresh: function(frm) {
        if (frm.doc.source == 'DMS' && frm.doc.docstatus == 0) {  
            // Vô hiệu hóa bảng con (Không cho thêm/sửa/xóa hàng)
            frm.fields_dict["items"].grid.df.cannot_add_rows = true;
            frm.fields_dict["items"].grid.df.cannot_delete_rows = true;
            frm.refresh_field("items");
        }
    }
});

frappe.ui.form.on('Sales Order Item', {
    refresh: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (frm.doc.source == 'DMS' && frm.doc.docstatus == 0) {  
            // Danh sách các trường trong bảng con cần khóa
            let fields_to_disable = ["item_code", "qty", "rate", "amount"];
            fields_to_disable.forEach(field => {
                frappe.model.set_df_property(cdt, field, 'read_only', 1);
            });
        }
    }
});
